mapper.update:
UPDATE Worktype
SET end_time = #{end_time}
WHERE emp_idx = #{emp_idx} AND work_date = CURDATE();

mapper.select:
SELECT *
FROM Worktype
WHERE emp_idx = #{emp_idx} AND work_date = CURDATE();

SELECT *
FROM Worktype
WHERE emp_idx = #{emp_idx} AND work_date >= DATE_SUB(CURDATE(), INTERVAL 7 DAY);

SELECT *
FROM Worktype
WHERE work_date = CURDATE();
